def getCell(x, y):
    if x < 0 or y < 0:
        return 'stone'
    if y >= world.height or x >= world.width:
        return 'stone'
    return world.grid[y][x]

def getFrontPos():
    offset = DIRECTION_OFFSETS[world.direction]
    return [world.x + offset[0], world.y + offset[1]]

def addToInventory(item, count):
    if item in world.inventory:
        world.inventory[item] = world.inventory[item] + count
    else:
        world.inventory[item] = count

def removeFromInventory(item, count):
    if item in world.inventory:
        current = world.inventory[item]
        newCount = current - count
        if newCount <= 0:
            del world.inventory[item]
        else:
            world.inventory[item] = newCount

def hasItem(item):
    if item in world.inventory:
        return world.inventory[item] > 0
    return False

def getItemCount(item):
    if item in world.inventory:
        return world.inventory[item]
    return 0

def mine():
    pos = getFrontPos()
    cell = getCell(pos[0], pos[1])
    if cell != 'stone' and cell != 'diamond' and cell != 'dirt':
        return False
    if cell in TOOL_REQUIREMENTS:
        required = TOOL_REQUIREMENTS[cell]
        if world.equipped not in required:
            return False
    if cell in CELL_DROPS:
        drops = CELL_DROPS[cell]
        addToInventory(drops[0], drops[1])
    world.grid[pos[1]][pos[0]] = 'empty'
    return True

def place(blockType):
    pos = getFrontPos()
    cell = getCell(pos[0], pos[1])
    if not hasItem(blockType):
        return False
    if cell != 'empty':
        return False
    if blockType not in PLACEABLE:
        return False
    cellType = PLACEABLE[blockType]
    world.grid[pos[1]][pos[0]] = cellType
    removeFromInventory(blockType, 1)
    return True

def equip(toolName):
    validTools = ['none', 'wooden_axe', 'wooden_pickaxe', 'stone_pickaxe']
    if toolName not in validTools:
        return False
    if toolName != 'none' and not hasItem(toolName):
        return False
    world.equipped = toolName
    return True

def canCraft(itemName):
    if itemName not in RECIPES:
        return False
    recipe = RECIPES[itemName]
    ingredients = recipe[0]
    i = 0
    while i < len(ingredients):
        item = ingredients[i]
        count = ingredients[i + 1]
        if getItemCount(item) < count:
            return False
        i = i + 2
    return True

def blockAt(direction):
    dir = DIRECTION_MAP[direction]
    offset = DIRECTION_OFFSETS[dir]
    x = world.x + offset[0]
    y = world.y + offset[1]
    return getCell(x, y)

def blockFront():
    pos = getFrontPos()
    return getCell(pos[0], pos[1])

def isBlocked(direction):
    cell = blockAt(direction)
    return not isWalkable(cell)

def canMine():
    pos = getFrontPos()
    cell = getCell(pos[0], pos[1])
    if cell != 'stone' and cell != 'diamond' and cell != 'dirt':
        return False
    if cell in TOOL_REQUIREMENTS:
        required = TOOL_REQUIREMENTS[cell]
        if world.equipped not in required:
            return False
    return True

def canChop():
    pos = getFrontPos()
    return getCell(pos[0], pos[1]) == 'tree'
